import React from 'react';
import { useState } from 'react';
import useToggle from './hooks/useToggle';

export default function Toggler() {
	const [isHappy, setIsHappy] = useToggle(true);
	const [isHeartbroken, setIsHeartbroken] = useState(true);

	return (
		<div>
			<h1>Toggler custom hook</h1>
			<h1 onClick={setIsHappy}>{isHappy ? 'üòÇ' : 'üò≠'}</h1>
			<h1 onClick={setIsHeartbroken}>{isHeartbroken ? 'üíî' : '‚ù§Ô∏è‚Äçüî•'}</h1>
		</div>
	);
}
